    "source": "define_synthdef(\"vocal_chop\", |builder| {\n    builder\n        .param(\"freq\", 440.0)\n        .param(\"amp\", 0.5)\n        .param(\"gate\", 1.0)\n        .param(\"vowel\", 0.5) // Vowel type\n        .body(|freq, amp, gate, vowel| {\n            // Short percussive envelope for chops\n            let envelope = env_asr(0.01, 1.0, 0.1);\n            let env = NewEnvGenBuilder(envelope, gate)\n                .with_done_action(2.0)\n                .build();\n\n            // Bright harmonically rich source\n            let saw = saw_ar(freq);\n            let pulse = saw_ar(freq * 1.001);\n            let source = (saw + pulse) * 0.5;\n\n            // Vowel formants (simplified)\n            let f1 = 300.0 + vowel * 500.0;\n            let f2 = 900.0 + (1.0 - vowel) * 700.0;\n\n            let formant1 = rlpf_ar(source, f1, 0.12);\n            let formant2 = rlpf_ar(source, f2, 0.1) * 0.6;\n\n            let vocal = formant1 + formant2;\n\n            // Slight pitch envelope for attack character\n            let pitch_env_data = env_perc(0.001, 0.05);\n            let pitch_env = NewEnvGenBuilder(pitch_env_data, dc_ar(1.0)).build();\n            let pitched = sin_osc_ar(freq * (1.0 + pitch_env * 0.05)) * 0.1;\n\n            (vocal + pitched) * env * amp\n        })\n});",
